BEGIN
	IF _elementotipo = 1 THEN

		SELECT 
			usu.id,
            _elementotipo as elementotipo,
            "Usuario" as elemento,
			ucase(usu.username) as nombre,
            usu.estado estadoactual,
            CASE usu.estado when 1 then 'Desactivar' else 'Activar' end as estado,
			count(proc.id) as relacion
