    usu.id IN (
    SELECT
		aux_col.colega_id
	FROM
		aquaweb.colegas as aux_col
	WHERE
		aux_col.usuario_id = _idUsuario and 
		aux_col.estado = 1

	UNION ALL

	SELECT
		aux_col.usuario_id
	FROM
		aquaweb.colegas as aux_col
	WHERE
		aux_col.colega_id = _idUsuario and 
        aux_col.estado = 1
    );
END ;;
