		FROM
			aquaweb.colegas as aux_col
		WHERE
			aux_col.colega_id = _usuarioid  and 
			aux_col.estado = 1
		)
	UNION ALL
		SELECT 
			"3" AS tipo, 
			proc.id AS id,
            proc.nombre as nombre,
            proc.usuario_id as usuario_id,
            "" as imagen, 
			proc.created_at as creado 
		FROM 
			aquaweb.proceso AS proc
