	usu.created_at,
	usu.id as idusuario,
	usu.username as usuario,
	tus.nombre as rol,
	UPPER(CONCAT(usu.primer_nombre, ' ',usu.segundo_nombre, ' ',usu.primer_apellido, ' ',usu.segundo_apellido, ' ')) As nombreusuario,
    UPPER(usu.primer_nombre) AS primer_nombre,
